$(function(){
	$('#open-notifications').click(function(){
		if($(this).attr('data-unread')>0)
		{
			$.ajax({
				type: 'POST',
				url: '/People/resetNotifyCounter',
				success: function(response){
					$(this).attr('data-unread', 0);
					$('.notify-counter').remove();
				}	
			});
		}
	});
	
	$('.notify-scroll').slimScroll({
        height: 'auto'
    });
	
});